mapper.insert:
INSERT INTO MEMBER (
memberId, pw, email, nickName, name, phoneNumber, inDate, memberImage,
gradeId
)
VALUES (
#{memberId}, #{pw}, #{email}, #{nickName}, #{name},
#{phoneNumber},
SYSDATE, #{memberImage, jdbcType=VARCHAR}, #{gradeId}
)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE memberId = #{memberId} AND pw = #{pw}
